mapper.select:
with w_modet as( SELECT modet.group_id as group_id, modet.hos_id as hos_id, modet.copy_code as copy_code, modet.out_id as out_id, modet.out_detail_id as out_detail_id, modet.inv_id as inv_id, modet.inv_no as inv_no, modet.batch_sn as batch_sn, modet.batch_no as batch_no, modet.price as price, modet.amount as amount, modet.sale_price as sale_price, modet.sale_money as sale_money, modet.sell_price as sell_price, modet.sell_money as sell_money, modet.allot_price as allot_price, modet.allot_money as allot_money, modet.amount_money as amount_money, modet.bar_code as bar_code, modet.inva_date as inva_date, modet.disinfect_date as disinfect_date, modet.location_id as location_id, modet.note as note, mid.inv_code as inv_code, mid.inv_name as inv_name, mid.inv_model as inv_model, mid.unit_code as unit_code, hu.unit_name as unit_name, mld.location_code as location_code, mld.location_name as location_name, mfb.left_amount as cur_amount FROM MED_OUT_DETAIL modet left join med_inv_dict mid on modet.group_id = mid.group_id and modet.hos_id = mid.hos_id and modet.copy_code = mid.copy_code and modet.inv_id = mid.inv_id and modet.inv_no = mid.inv_no LEFT JOIN hos_unit hu ON mid.group_id = hu.group_id and mid.hos_id = hu.hos_id and mid.unit_code = hu.unit_code LEFT JOIN med_location_dict mld on modet.group_id = mld.group_id and modet.hos_id = mld.hos_id and modet.copy_code = mld.copy_code and modet.location_id = mld.location_id and mld.is_stop=0 left join med_fifo_balance mfb on modet.group_id = mfb.group_id and modet.hos_id = mfb.hos_id and modet.copy_code = mfb.copy_code and modet.inv_id = mfb.inv_id and modet.batch_sn= mfb.batch_sn and modet.batch_no = mfb.batch_no and modet.bar_code = mfb.bar_code and mfb.store_id = #{store_id,jdbcType=INTEGER} where modet.group_id = #{group_id,jdbcType=INTEGER} AND modet.hos_id = #{hos_id,jdbcType=INTEGER} AND modet.copy_code = #{copy_code,jdbcType=VARCHAR} AND modet.out_id = #{out_id,jdbcType=INTEGER} order by modet.out_detail_id asc ), w_mod as( select inv_id, batch_sn, batch_no, bar_code, sum(amount) as amount from( --出库未确认单据 select inv_id, batch_sn, batch_no, bar_code, sum(nvl(amount, 0)) as amount from med_out_main mom left join med_out_detail medod on mom.group_id = medod.group_id and mom.hos_id = medod.hos_id and mom.copy_code = medod.copy_code and mom.out_id = medod.out_id where medod.group_id = #{group_id,jdbcType=INTEGER} AND medod.hos_id = #{hos_id,jdbcType=INTEGER} AND medod.copy_code = #{copy_code,jdbcType=VARCHAR} AND mom.store_id = #{store_id,jdbcType=INTEGER}
AND mom.out_id <> #{out_id,jdbcType=INTEGER} --出库单加载使用 AND mom.state < 3 AND mom.bus_type_code <> '21' AND medod.amount_money > 0
group by inv_id, batch_sn, batch_no, bar_code --退货未确认单据 union all select inv_id as inv_id, batch_sn as batch_sn, batch_no as batch_no, bar_code as bar_code, -1 * sum(nvl(amount, 0)) as amount from med_in_main mim left join med_in_detail medid on mim.group_id = medid.group_id and mim.hos_id = medid.hos_id and mim.copy_code = medid.copy_code and mim.in_id = medid.in_id where medid.group_id = #{group_id,jdbcType=INTEGER} AND medid.hos_id = #{hos_id,jdbcType=INTEGER} AND medid.copy_code = #{copy_code,jdbcType=VARCHAR} AND mim.store_id = #{store_id,jdbcType=INTEGER}
AND mim.state < 3
AND mim.bus_type_code in ('10','12','16','22') and medid.amount_money < 0 group by inv_id, batch_sn, batch_no, bar_code ) group by inv_id, batch_sn, batch_no, bar_code order by inv_id asc ) select w_modet.group_id as group_id, w_modet.hos_id as hos_id, w_modet.copy_code as copy_code, w_modet.out_id as out_id, w_modet.inv_id as inv_id, w_modet.inv_no as inv_no, w_modet.batch_no as batch_no, w_modet.price as price, sum(nvl(w_modet.cur_amount, 0)) as cur_amount, sum(nvl(w_modet.cur_amount, 0) - nvl(w_mod.amount, 0)) AS imme_amount, sum(nvl(w_modet.amount, 0)) as amount, sum(nvl(w_modet.amount_money, 0)) as amount_money, w_modet.sale_price as sale_price, sum(nvl(w_modet.sale_money, 0)) as sale_money, w_modet.sell_price as sell_price, sum(nvl(w_modet.sell_money, 0)) as sell_money, w_modet.allot_price as allot_price, sum(nvl(w_modet.allot_money, 0)) as allot_money, w_modet.bar_code as bar_code, w_modet.inva_date as inva_date, w_modet.disinfect_date as disinfect_date, w_modet.location_id as location_id, w_modet.note as note, w_modet.inv_code as inv_code, w_modet.inv_name as inv_name, w_modet.inv_model as inv_model, w_modet.unit_code as unit_code, w_modet.unit_name as unit_name, w_modet.location_code as location_code, w_modet.location_name as location_name, sum(nvl(w_modet.amount, 0)) as sum_amount, '['||to_char(wm_concat('{"out_detail_id":'||w_modet.out_detail_id||',"inv_id":'||w_modet.inv_id ||',"inv_code":"'||to_char(w_modet.inv_code)||'","inv_name":"'||to_char(w_modet.inv_name) ||'","batch_sn":'||w_modet.batch_sn||',"cur_amount":'||w_modet.cur_amount ||',"imme_amount":'||(nvl(w_modet.cur_amount,0)-nvl(w_mod.amount,0)) ||',"amount":'||w_modet.amount ||',"price":'||w_modet.price||',"amount_money":'||w_modet.amount_money ||',"sale_price":'||w_modet.sale_price||',"sale_money":'||w_modet.sale_money ||',"sell_price":'||w_modet.sell_price||',"sell_money":'||w_modet.sell_money ||'}'))||']' inv_detail_data from w_modet w_modet left join w_mod w_mod on w_modet.inv_id = w_mod.inv_id and w_modet.batch_sn= w_mod.batch_sn and w_modet.batch_no= w_mod.batch_no and w_modet.bar_code= w_mod.bar_code group by w_modet.group_id, w_modet.hos_id, w_modet.copy_code, w_modet.out_id, w_modet.inv_id, w_modet.inv_no, w_modet.batch_no, w_modet.price, w_modet.sale_price, w_modet.sell_price, w_modet.allot_price, w_modet.bar_code, w_modet.inva_date, w_modet.disinfect_date, w_modet.location_id, w_modet.note, w_modet.inv_code, w_modet.inv_name, w_modet.inv_model, w_modet.unit_code, w_modet.unit_name, w_modet.location_code, w_modet.location_name order by inv_detail_data

with w_modet as( SELECT modet.group_id as group_id, modet.hos_id as hos_id, modet.copy_code as copy_code, modet.out_id as out_id, modet.detail_id as detail_id, modet.inv_id as inv_id, modet.inv_no as inv_no, modet.batch_sn as batch_sn, modet.batch_no as batch_no, nvl(modet.price,0) as price, nvl(modet.amount,0) as amount, nvl(modet.sale_price,0) as sale_price, nvl(modet.sale_money,0) as sale_money, nvl(modet.sell_price,0) as sell_price, nvl(modet.sell_money,0) as sell_money, nvl(modet.allot_price,0) as allot_price, nvl(modet.allot_money,0) as allot_money, nvl(modet.amount_money,0) as amount_money, modet.bar_code as bar_code, modet.inva_date as inva_date, modet.disinfect_date as disinfect_date, modet.location_id as location_id, modet.note as note, mid.inv_code as inv_code, mid.inv_name as inv_name, mid.inv_model as inv_model, mid.unit_code as unit_code, hu.unit_name as unit_name, mld.location_code as location_code, mld.location_name as location_name, mfb.left_amount as cur_amount FROM med_affi_out_detail modet left join med_inv_dict mid on modet.group_id = mid.group_id and modet.hos_id = mid.hos_id and modet.copy_code = mid.copy_code and modet.inv_id = mid.inv_id and modet.inv_no = mid.inv_no LEFT JOIN hos_unit hu ON mid.group_id = hu.group_id and mid.hos_id = hu.hos_id and mid.unit_code = hu.unit_code and hu.is_stop = 0 LEFT JOIN med_location_dict mld on modet.group_id = mld.group_id and modet.hos_id = mld.hos_id and modet.copy_code = mld.copy_code and modet.location_id = mld.location_id and mld.is_stop=0 left join med_affi_fifo mfb on modet.group_id = mfb.group_id and modet.hos_id = mfb.hos_id and modet.copy_code = mfb.copy_code and modet.inv_id = mfb.inv_id and modet.batch_sn= mfb.batch_sn and modet.batch_no = mfb.batch_no and modet.bar_code = mfb.bar_code and mfb.store_id = #{store_id,jdbcType=INTEGER} where modet.group_id = #{group_id,jdbcType=INTEGER} AND modet.hos_id = #{hos_id,jdbcType=INTEGER} AND modet.copy_code = #{copy_code,jdbcType=VARCHAR} AND modet.out_id = #{out_id,jdbcType=INTEGER} order by modet.detail_id asc ), w_mod as( select inv_id, batch_sn, batch_no, bar_code, sum(amount) as amount from( --出库未确认单据 select inv_id as inv_id, batch_sn as batch_sn, batch_no as batch_no, bar_code as bar_code, sum(nvl(amount, 0)) as amount from med_affi_out mom left join med_affi_out_detail medod on mom.group_id = medod.group_id and mom.hos_id = medod.hos_id and mom.copy_code = medod.copy_code and mom.out_id = medod.out_id where medod.group_id = #{group_id,jdbcType=INTEGER} AND medod.hos_id = #{hos_id,jdbcType=INTEGER} AND medod.copy_code = #{copy_code,jdbcType=VARCHAR} AND mom.store_id = #{store_id,jdbcType=INTEGER}
AND mom.out_id <> #{out_id,jdbcType=INTEGER} --出库单加载使用 AND mom.state < 3 AND mom.bus_type_code <> '30'
group by inv_id,batch_sn,batch_no,bar_code --退货未确认单据 union all select inv_id as inv_id, batch_sn as batch_sn, batch_no as batch_no, bar_code as bar_code, sum(nvl(amount, 0)) as amount from med_affi_in mim left join med_affi_in_detail medid on mim.group_id = medid.group_id and mim.hos_id = medid.hos_id and mim.copy_code = medid.copy_code and mim.in_id = medid.in_id where medid.group_id = #{group_id,jdbcType=INTEGER} AND medid.hos_id = #{hos_id,jdbcType=INTEGER} AND medid.copy_code = #{copy_code,jdbcType=VARCHAR} AND mom.store_id = #{store_id,jdbcType=INTEGER}
AND mim.state < 3
AND mim.bus_type_code = '29' group by inv_id,batch_sn,batch_no,bar_code ) group by inv_id, batch_sn, batch_no, bar_code order by inv_id asc ) select w_modet.group_id as group_id, w_modet.hos_id as hos_id, w_modet.copy_code as copy_code, w_modet.out_id as out_id, w_modet.inv_id as inv_id, w_modet.inv_no as inv_no, w_modet.batch_no as batch_no, w_modet.price as price, sum(nvl(w_modet.cur_amount, 0)) as cur_amount, sum(nvl(w_modet.cur_amount, 0) - nvl(w_mod.amount, 0)) AS imme_amount, sum(nvl(w_modet.amount, 0)) as amount, sum(nvl(w_modet.amount_money, 0)) as amount_money, w_modet.sale_price as sale_price, sum(nvl(w_modet.sale_money, 0)) as sale_money, w_modet.sell_price as sell_price, sum(nvl(w_modet.sell_money, 0)) as sell_money, w_modet.allot_price as allot_price, sum(nvl(w_modet.allot_money, 0)) as allot_money, w_modet.bar_code as bar_code, w_modet.inva_date as inva_date, w_modet.disinfect_date as disinfect_date, w_modet.location_id as location_id, w_modet.note as note, w_modet.inv_code as inv_code, w_modet.inv_name as inv_name, w_modet.inv_model as inv_model, w_modet.unit_code as unit_code, w_modet.unit_name as unit_name, w_modet.location_code as location_code, w_modet.location_name as location_name, sum(nvl(w_modet.amount, 0)) as sum_amount, '['||to_char(wm_concat('{"detail_id":'||w_modet.detail_id||',"inv_id":'||w_modet.inv_id ||',"inv_code":"'||to_char(w_modet.inv_code)||'","inv_name":"'||to_char(w_modet.inv_name) ||'","batch_sn":'||w_modet.batch_sn||',"cur_amount":'||w_modet.cur_amount ||',"imme_amount":'||(nvl(w_modet.cur_amount,0)-nvl(w_mod.amount,0)) ||',"amount":'||w_modet.amount ||',"price":'||w_modet.price||',"amount_money":'||w_modet.amount_money ||',"sale_price":'||w_modet.sale_price||',"sale_money":'||w_modet.sale_money ||',"sell_price":'||w_modet.sell_price||',"sell_money":'||w_modet.sell_money ||'}'))||']' inv_detail_data from w_modet w_modet left join w_mod w_mod on w_modet.inv_id = w_mod.inv_id and w_modet.batch_sn= w_mod.batch_sn and w_modet.batch_no= w_mod.batch_no and w_modet.bar_code= w_mod.bar_code group by w_modet.group_id, w_modet.hos_id, w_modet.copy_code, w_modet.out_id, w_modet.inv_id, w_modet.inv_no, w_modet.batch_no, w_modet.price, w_modet.sale_price, w_modet.sell_price, w_modet.allot_price, w_modet.bar_code, w_modet.inva_date, w_modet.disinfect_date, w_modet.location_id, w_modet.note, w_modet.inv_code, w_modet.inv_name, w_modet.inv_model, w_modet.unit_code, w_modet.unit_name, w_modet.location_code, w_modet.location_name order by inv_detail_data

SELECT a.apply_id as id, a.group_id, a.hos_id, a.copy_code, a.apply_id, a.detail_id, a.inv_id, a.inv_no, b.inv_code, b.inv_name, b.inv_model, c.unit_name, d.fac_name, b.is_com, a.app_amount, g.rela_amount, e.cur_amount common_amount, f.cur_amount affi_amount, nvl(b.plan_price, 0) price, a.app_amount*nvl(b.plan_price, 0) amount_money, a.note,b.bid_code FROM med_apply_detail a left join med_inv_dict b on a.group_id = b.group_id and a.hos_id = b.hos_id and a.copy_code = b.copy_code and a.inv_id = b.inv_id and a.inv_no = b.inv_no left join hos_unit c on b.group_id = c.group_id and b.hos_id = c.hos_id and b.unit_code = c.unit_code left join hos_fac_dict d on b.group_id = d.group_id and b.hos_id = d.hos_id and b.fac_id = d.fac_id and b.fac_no = d.fac_no left join med_inv_hold e on a.group_id = e.group_id and a.hos_id = e.hos_id and a.copy_code = e.copy_code and a.inv_id = e.inv_id and e.store_id = #{store_id,jdbcType=INTEGER} left join med_affi_inv_hold f on a.group_id = f.group_id and a.hos_id = f.hos_id and a.copy_code = f.copy_code and a.inv_id = f.inv_id and f.store_id = #{store_id,jdbcType=INTEGER} LEFT JOIN ( select app_detail_id, sum(nvl(rela_amount, 0)) rela_amount from med_apply_out_rela where group_id = #{group_id,jdbcType=INTEGER} and hos_id = #{hos_id,jdbcType=INTEGER} and copy_code = #{copy_code,jdbcType=VARCHAR} and apply_id = #{apply_id,jdbcType=INTEGER} and rela_type <> 5 group by app_detail_id ) g on a.detail_id = g.app_detail_id WHERE a.group_id = #{group_id,jdbcType=INTEGER} and a.hos_id = #{hos_id,jdbcType=INTEGER} and a.copy_code = #{copy_code,jdbcType=VARCHAR}
and a.apply_id in (${paraId})
and a.apply_id=#{apply_id,jdbcType=NUMERIC}

select a.group_id, a.hos_id, a.copy_code, rela_type, rela_id, sum(rela_amount)
from med_apply_out_rela a
left join med_require_main b
	on a.group_id = b.group_id
	and a.hos_id = b.hos_id
	and a.copy_code = b.copy_code
	and a.rela_id = b.req_id
where a.group_id = #{group_id, jdbcType = INTEGER}
	and a.hos_id = #{hos_id, jdbcType = INTEGER}
	and a.copy_code = #{copy_code, jdbcType = VARCHAR}
	and apply_id = #{apply_id, jdbcType = INTEGER}
	and rela_type = 5
group by a.group_id, a.hos_id, a.copy_code, rela_type, rela_id
