mapper.select:
SELECT decode(GROUPING_ID(hsd.sup_code,hsd.sup_name),0,hsd.sup_code,1,to_char('供应商小计'),to_char('总合计')) sup_code, hsd.sup_name, midd.inv_code, midd.inv_name, midd.inv_model , hu.unit_name, SUM(mid.amount) AS amount,
mid.price , SUM(mid.amount_money) AS amount_money, mtd.med_type_name FROM med_in_detail mid LEFT JOIN med_in_main mim ON mid.group_id = mim.group_id AND mid.hos_id = mim.hos_id AND mid.copy_code = mim.copy_code AND mid.in_id = mim.in_id LEFT JOIN hos_sup_dict hsd ON hsd.group_id = mim.group_id AND hsd.hos_id = mim.hos_id AND hsd.sup_id = mim.sup_id AND hsd.sup_no = mim.sup_no LEFT JOIN med_inv_dict midd ON mid.group_id = midd.group_id AND mid.hos_id = midd.hos_id AND mid.inv_id = midd.inv_id
AND midd.is_stop = '0' LEFT JOIN hos_unit hu ON hu.group_id = midd.group_id AND hu.hos_id = midd.hos_id AND hu.unit_code = midd.unit_code LEFT JOIN med_type_dict mtd ON mtd.group_id = midd.group_id AND mtd.hos_id = midd.hos_id AND mtd.copy_code = midd.copy_code AND mtd.med_type_id = midd.med_type_id AND mtd.med_type_no = midd.med_type_no
mim.state = '3'
and mim.bus_type_code in (2,12,47,48)
and mim.group_id = #{group_id}
and mim.hos_id = #{hos_id}
and mim.copy_code = #{copy_code}
AND mim.confirm_date between to_date(#{begin_confirm_date},'yyyy-MM-dd') and to_date(#{end_confirm_date},'yyyy-MM-dd')
and to_char(mim.store_id) in ( select perm_code from v_user_data_perm where group_id = #{group_id,jdbcType=INTEGER} and hos_id = #{hos_id,jdbcType=INTEGER} and table_code = 'HOS_STORE_DICT' and user_id = #{user_id,jdbcType=INTEGER} and is_read = 1 and is_write = 1 )
and mim.store_id = #{store_id}
and mim.store_no= #{store_no}
and mtd.med_type_id = #{med_type_id}
and mtd.med_type_no= #{med_type_no}
AND mim.in_date between to_date(#{begin_in_date},'yyyy-MM-dd') and to_date(#{end_in_date},'yyyy-MM-dd')
and mim.sup_id = #{sup_id}
and mim.sup_no= #{sup_no}
and ( midd.inv_id like '%${inv_id}%' or midd.inv_code like '%${inv_id}%' or upper(midd.inv_name) like '%${inv_id}%' or upper(midd.inv_model) like '%${inv_id}%' or upper(midd.spell_code) like '%${inv_id}%' or upper(midd.wbx_code) like '%${inv_id}%' or lower(midd.spell_code) like '%${inv_id}%' or lower(midd.wbx_code) like '%${inv_id}%' or lower(midd.inv_name) like '%${inv_id}%' )
and midd.is_charge = #{is_charge}
and (midd.inv_model like '%${inv_model}%' or midd.inv_name like '%${inv_model}%' or midd.inv_code like '%${inv_model}%' or midd.spell_code like '%${inv_model}%' or midd.wbx_code like '%${inv_model}%'
)
group by grouping sets ((hsd.sup_code, hsd.sup_name, midd.inv_code, midd.inv_name, midd.inv_model, hu.unit_name, mtd.med_type_name, mid.price),hsd.sup_code,null)
