mapper.select:
SELECT psf.SP_CODE, psf.SIGN_DATE, hs.SUP_NAME, psf.PACT_CODE, mf.PACT_NAME, psf.SP_MONEY, dd2.VALUE_NAME AS party_name, psf.STATE, psf.NOTE, dd.VALUE_NAME AS state_name, he.USER_NAME AS maker_name, he1.USER_NAME AS checker_name, he2.USER_NAME AS confirmer_name FROM PACT_SP_FKHT psf LEFT JOIN PACT_MAIN_FKHT mf ON psf.GROUP_ID = mf.GROUP_ID AND psf.HOS_ID = mf.HOS_ID AND psf.COPY_CODE = mf.COPY_CODE AND psf.PACT_CODE = mf.PACT_CODE LEFT JOIN HOS_SUP hs ON hs.GROUP_ID = psf.GROUP_ID AND hs.HOS_ID = psf.HOS_ID AND hs.SUP_ID = psf.SUP_NO LEFT JOIN sys_user he ON he.GROUP_ID = psf.GROUP_ID AND he.HOS_ID = psf.HOS_ID AND he.USER_ID = psf.MAKER LEFT JOIN sys_user he1 ON he1.GROUP_ID = psf.GROUP_ID AND he1.HOS_ID = psf.HOS_ID AND he1.USER_ID = psf.CHECKER LEFT JOIN sys_user he2 ON he2.GROUP_ID = psf.GROUP_ID AND he2.HOS_ID = psf.HOS_ID AND he2.USER_ID = psf.CONFIRMER LEFT JOIN PACT_DICT_DATA dd ON dd.VALUE_CODE = psf.STATE AND dd.F_CODE = 'STATE' LEFT JOIN PACT_DICT_DATA dd2 ON dd2.VALUE_CODE = psf.PARTY AND dd2.F_CODE = 'PARTY'
psf.GROUP_ID=#{group_id,jdbcType=NUMERIC} and psf.HOS_ID=#{hos_id,jdbcType=NUMERIC} and psf.COPY_CODE=#{copy_code,jdbcType=VARCHAR}
and psf.STATE=#{state,jdbcType=VARCHAR}
and psf.FIRST_MAN=#{first_man,jdbcType=NUMERIC}
and psf.party=#{party,jdbcType=NUMERIC}
and psf.note like '%'||#{note,jdbcType=VARCHAR}||'%'
and mf.pact_name like '%'||#{pact_name,jdbcType=VARCHAR}||'%'
and psf.pact_code like '%'||upper(#{pact_code,jdbcType=VARCHAR})||'%'
and mf.pact_type_code=#{pact_type_code,jdbcType=VARCHAR}
and psf.sup_no=#{sup_no,jdbcType=VARCHAR}
and psf.SIGN_DATE BETWEEN to_date(#{start_date,jdbcType=TIMESTAMP},'yyyy-MM-dd') AND to_date(#{end_date,jdbcType=TIMESTAMP},'yyyy-MM-dd')

SELECT psf.SP_CODE, psf.SIGN_DATE, hs.SUP_NAME, psf.PACT_CODE, mf.PACT_NAME, psf.SP_MONEY, dd2.VALUE_NAME AS party_name, psf.STATE, psf.NOTE, dd.VALUE_NAME AS state_name, he.USER_NAME AS maker_name, he1.USER_NAME AS checker_name, he2.USER_NAME AS confirmer_name FROM PACT_SP_FKHT psf LEFT JOIN PACT_MAIN_FKHT mf ON psf.GROUP_ID = mf.GROUP_ID AND psf.HOS_ID = mf.HOS_ID AND psf.COPY_CODE = mf.COPY_CODE AND psf.PACT_CODE = mf.PACT_CODE LEFT JOIN HOS_SUP hs ON hs.GROUP_ID = psf.GROUP_ID AND hs.HOS_ID = psf.HOS_ID AND hs.SUP_ID = psf.SUP_NO LEFT JOIN sys_user he ON he.GROUP_ID = psf.GROUP_ID AND he.HOS_ID = psf.HOS_ID AND he.USER_ID = psf.MAKER LEFT JOIN sys_user he1 ON he1.GROUP_ID = psf.GROUP_ID AND he1.HOS_ID = psf.HOS_ID AND he1.USER_ID = psf.CHECKER LEFT JOIN sys_user he2 ON he2.GROUP_ID = psf.GROUP_ID AND he2.HOS_ID = psf.HOS_ID AND he2.USER_ID = psf.CONFIRMER LEFT JOIN PACT_DICT_DATA dd ON dd.VALUE_CODE = psf.STATE AND dd.F_CODE = 'STATE' LEFT JOIN PACT_DICT_DATA dd2 ON dd2.VALUE_CODE = psf.PARTY AND dd2.F_CODE = 'PARTY'
psf.GROUP_ID=#{group_id,jdbcType=NUMERIC} and psf.HOS_ID=#{hos_id,jdbcType=NUMERIC} and psf.COPY_CODE=#{copy_code,jdbcType=VARCHAR}
and psf.STATE=#{state,jdbcType=VARCHAR}
and psf.FIRST_MAN=#{first_man,jdbcType=NUMERIC}
and psf.party=#{party,jdbcType=NUMERIC}
and psf.note like '%'||#{note,jdbcType=VARCHAR}||'%'
and mf.pact_name like '%'||#{pact_name,jdbcType=VARCHAR}||'%'
and psf.pact_code like upper(#{pact_code,jdbcType=VARCHAR})||'%'
and mf.pact_type_code=#{pact_type_code,jdbcType=VARCHAR}
and psf.sup_no=#{sup_no,jdbcType=VARCHAR}
and psf.SIGN_DATE BETWEEN to_date(#{start_date,jdbcType=TIMESTAMP},'yyyy-MM-dd') AND to_date(#{end_date,jdbcType=TIMESTAMP},'yyyy-MM-dd')

mapper.update:
UPDATE PACT_SP_FKHT SET

	confirmer=#{confirmer,jdbcType=NUMERIC},
	confirm_date=#{confirm_date,jdbcType=TIMESTAMP},



		checker=#{checker,jdbcType=NUMERIC},
		check_date=#{check_date,jdbcType=TIMESTAMP},

	confirmer=#{confirmer,jdbcType=NUMERIC},
	confirm_date=#{confirm_date,jdbcType=TIMESTAMP},


	checker=#{checker,jdbcType=NUMERIC},
	check_date=#{check_date,jdbcType=TIMESTAMP},

state=#{state,jdbcType=NUMERIC}
WHERE
GROUP_ID = #{group_id,jdbcType=NUMERIC}
AND HOS_ID =#{hos_id,jdbcType=NUMERIC} 
AND COPY_CODE = #{copy_code,jdbcType=VARCHAR} and

	pact_code = #{item.pact_code,jdbcType=VARCHAR}
	AND sp_code = #{item.sp_code,jdbcType=VARCHAR}

UPDATE PACT_SP_FKHT SET
sign_date=to_timestamp(#{sign_date,jdbcType=TIMESTAMP},'yyyy-mm--dd hh24:mi:ss.ff'),
party=#{party,jdbcType=VARCHAR},
sp_money=#{sp_money,jdbcType=NUMERIC},
first_man=#{first_man,jdbcType=VARCHAR},
second_man=#{second_man,jdbcType=VARCHAR},
note=#{note,jdbcType=NUMERIC},
reason=#{reason,jdbcType=VARCHAR},
depend=#{depend,jdbcType=VARCHAR}
WHERE
GROUP_ID = #{group_id,jdbcType=NUMERIC}
AND HOS_ID =#{hos_id,jdbcType=NUMERIC}
AND COPY_CODE = #{copy_code,jdbcType=VARCHAR}
AND sp_code = #{sp_code,jdbcType=VARCHAR}
and	pact_code=#{pact_code,jdbcType=VARCHAR}

mapper.delete:
DELETE FROM PACT_SP_FKHT


		GROUP_ID = #{item.group_id,jdbcType=NUMERIC}
		and HOS_ID = #{item.hos_id,jdbcType=NUMERIC}
		AND COPY_CODE = #{item.copy_code,jdbcType=VARCHAR}
		AND pact_code = #{item.pact_code,jdbcType=VARCHAR}
		AND sp_code = #{item.sp_code,jdbcType=VARCHAR}

DELETE FROM PACT_SP_FKHT


		GROUP_ID = #{item.group_id,jdbcType=NUMERIC}
		and HOS_ID = #{item.hos_id,jdbcType=NUMERIC}
		AND COPY_CODE = #{item.copy_code,jdbcType=VARCHAR}
		AND pact_code = #{item.pact_code,jdbcType=VARCHAR}

DELETE FROM
PACT_SP_FKHT WHERE
GROUP_ID = #{group_id,jdbcType=NUMERIC}
and HOS_ID = #{hos_id,jdbcType=NUMERIC}
AND COPY_CODE = #{copy_code,jdbcType=VARCHAR}
AND pact_code = #{pact_code,jdbcType=VARCHAR}
