mapper.select:

        SELECT 
            group_id,
            hos_id,
            copy_code,
            nature_code,
            nature_name,
            spell_code,
            wbx_code,
            is_stop
        FROM prm_dept_nature 


                    AND group_id = #{group_id}


                    and hos_id = #{hos_id} 


                    AND copy_code = #{copy_code}


                    AND nature_code = #{nature_code}


                    AND nature_name = #{nature_name}


                    AND spell_code = #{spell_code}


                    AND wbx_code = #{wbx_code}


                    AND is_stop = #{is_stop}


				order by group_id asc
