mapper.insert:
INSERT INTO PRM_FORMULA ( group_id , hos_id , copy_code , formula_code , formula_name , formula_method_chs , formula_method_eng , is_stop
) select PRM_FORMULA_SEQ.nextval , t.* from(
select #{item.group_id} , #{item.hos_id} , #{item.copy_code} , #{item.formula_code} , #{item.formula_name} , #{item.formula_method_chs} , #{item.formula_method_eng} , #{item.is_stop}
)t

mapper.update:

UPDATE prm_formula SET
formula_name = #{formula_name}
,
formula_method_chs = #{formula_method_chs}
,
formula_method_eng = #{formula_method_eng}
,
is_stop = #{is_stop}

WHERE 
group_id = #{group_id}
 and 
hos_id = #{hos_id}
 and 
copy_code = #{copy_code}
 and 
formula_code = #{formula_code}

update.foreach:
UPDATE prm_formula SET
formula_name = #{formula_name}
,
formula_method_chs = #{formula_method_chs}
,
formula_method_eng = #{formula_method_eng}
,
is_stop = #{is_stop}

WHERE 
group_id = #{item.group_id}
 and 
hos_id = #{item.hos_id}
 and 
copy_code = #{item.copy_code}
 and 
formula_code = #{item.formula_code}

mapper.delete:
DELETE FROM prm_formula WHERE

		group_id = #{item.group_id}
		 and 
		hos_id = #{item.hos_id}
		 and 
		copy_code = #{item.copy_code}
		 and 
		formula_code = #{item.formula_code}

mapper.select:

SELECT 
group_id,
hos_id,
copy_code,
formula_code,
formula_name,
formula_method_chs,
formula_method_eng,
is_stop
FROM PRM_FORMULA 


AND group_id = #{group_id}


AND hos_id = #{hos_id}


AND copy_code = #{copy_code}


AND formula_code = #{formula_code}


AND formula_name = #{formula_name}


AND formula_method_chs = #{formula_method_chs}


AND formula_method_eng = #{formula_method_eng}


AND is_stop = #{is_stop}


order by group_id asc
