mapper.insert:
INSERT INTO PRM_FUN_STACK ( group_id , hos_id , copy_code , target_code , fun_para_code , fun_para_value
) select t.* from(
select #{item.group_id} , #{item.hos_id} , #{item.copy_code} , #{item.target_code} , #{item.fun_para_code} , #{item.fun_para_value} from dual
)t

mapper.update:

UPDATE prm_fun_stack SET
fun_para_value = #{fun_para_value}

WHERE 
group_id = #{group_id}
 and 
hos_id = #{hos_id}
 and 
copy_code = #{copy_code}
 and 
target_code = #{target_code}
 and 
fun_para_code = #{fun_para_code}

update.foreach:
UPDATE prm_fun_stack SET
fun_para_value = #{fun_para_value}

WHERE 
group_id = #{item.group_id}
 and 
hos_id = #{item.hos_id}
 and 
copy_code = #{item.copy_code}
 and 
target_code = #{item.target_code}
 and 
fun_para_code = #{item.fun_para_code}

mapper.delete:
DELETE FROM prm_fun_stack WHERE

		group_id = #{item.group_id}
		 and 
		hos_id = #{item.hos_id}
		 and 
		copy_code = #{item.copy_code}
		 and 
		target_code = #{item.target_code}
		 and 
		fun_para_code = #{item.fun_para_code}

mapper.select:

SELECT 
group_id,
hos_id,
copy_code,
target_code,
fun_para_code,
fun_para_value
FROM PRM_FUN_STACK 


AND group_id = #{group_id}


AND hos_id = #{hos_id}


AND copy_code = #{copy_code}


AND target_code = #{target_code}


AND fun_para_code = #{fun_para_code}


AND fun_para_value = #{fun_para_value}


order by group_id asc
